INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (102,'1 bis rue Pierre et Marie Curie\n\nCLAMART\n',0,'','','',NULL,'','bl@ltm.fr','cf observationshttp://www.ltm.fr','LECLERCQ','06 80 05 56 52','01 46 29 06 65','le 28.06.13 : \"Nous ne travaillons plus que sur les sujets : Android, iOS, C++, HTML5 et Javascript.\"\r\n\r\n« Optimiser vos applications java, .net, c++ »\n•         « Programmation des applications en Multicore »\n: nous avons bien les supports de cours pour ces formations. Notre tarif  est de 800 € HT/Jour.\r\n\r\nDéveloppement ASP .NET 4.0 avancé :  je ne serai pas opérationnel avant Janvier 2011.\r\n\r\nSpring désolé je ne suis pas compétent.\r\n\r\nConcernant des formations Java et Dot Net, C++, Web Services, Corba, Optimisation, je le suis.\r\n\r\nDCPA nov 2010 pour Intra PLB = 20/20 pour le prof\r\n\r\nSera prêt sur Android à partir du 15/02\r\n\r\n\r\nNe fait pas Java temps réel et embarqué\r\n\r\nTarifs Inter Fafiec : LB : Il m’a dit qu’il mettrait 700 dans son prévisionnel mais on fera un peu au cas par cas.\r\n\r\nIl est OK pour le plafond à 900 pour l’inter normal (je lui avais proposé un système en escalier)\r\n\r\nNe fait pas delphi\r\n\r\n2. J\'ai aussi signalé que Alain Duglas et moi-même allons démarrer une association à partir de Septembre 2011.\r\n\r\nC\'est donc LTM qui dorénavant va gérer le planning d\'Alain. A partir de Septembre 2011, les dates de formations seront donc à réserver auprès de moi-même.\r\n\r\nLe tarif d\'Alain s\'alignera sur le mien à partir de Septembre.\r\n\r\nTous les cours que Bertrand Leclercq donne (et donc les supports) seront utilisables par Alain. (Exemple : Cours Androïd).\r\nAttention : Certains support de cours sont copyright Dassault Systèmes et ne sont pas utilisables.','couleur','Bertrand',15,'','',0);
INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (103,'15 B BD Gouvion\nSaint Cyr 75017 PARIS\n\n',0,'DEMOS, FITEC','','',NULL,'','sleenhardt@supmicro.com','','LEENHARDT','06 62 01 32 90','01 44 40 44 75','BLACKLIST LE 16/03 Car trop de désistement sauvages  \r\n800e HT/j sur le MSDT\r\n\r\n(connaît Fabien Munoz qu\'il apprécie), Fâché avec IB\nEchanges de mails puis discussion téléphonique avec LB le 10/11/08. Bon contact. Me propose un cours biztalk plus riche selon lui que sharepoint.Coordonnées par Edgar Maucourant en 2008. Discussions téléphoniques fin 2008 et le 10 juillet 2009 avec LB. Très calme et posé\nFait tous les cours Sharepoint développement et commence à investir dans biztalk qui lui semble plus puissant que sharepoint depuis 1 an environ. \nSe positionne suir d\'autres cours dont PHP avancé                                                                           \r\n==>Refuse de communiquer ces supports, le reprographie lui même Tu peux intervenir sur tous les cours sharepoint (supports personnels)\r\nEgalement C#, WPF, WF, WCF, Ajax, PHP Perfectionnement\r\nTu adores XML …\r\nTu as un magnifique cours Java Design Pattern\r\n\r\nSyngenta avril 2010 : 3TS, 1S\r\nWCF bof, suite M6461 fin avril chez PLB : \r\nil se trouve que le directeur informatique des personnes qui sont venues chez nous pour WCF est un ami de Pierre.\r\nIl ne savait pas qu’ils s’étaient inscrits chez nous …\r\n\r\nMême si les évals ne le montrent pas il nous a dit que les stagiaires avaient trouvé le prof très sympa mais le cours bof …\r\nDonc note dans LIEC qu’il faut l’utiliser en SHarepoint et pas trop en développement.\r\n\r\nPour le cours d’expertise Windows 2008 je serai  à 700 euros HT/jour,\r\n\r\nJuin 2011 pour septembre 2011 :\r\n\r\nJe fais encore pour vous mon tarif de l’année dernière mais a partir de septembre\r\nJe vais devoir être au même niveau qu’avec mes autres clients :\r\nMon tarif journalier est de 700 Euros HT/Jour quel que soit le stage, c’est ma moyenne.\r\n\r\nDans certains cas, je peux fournir un support de formation entre 20 a 30 euros par stagiaires,\r\nA voir en fonction du nombre de stagiaires.\r\n\r\nJe ne te cache pas que je suis déjà a 700 depuis longtemps sur toutes les demandes SharePoint 2010,\r\nJe vous ai fait une réduction 650  jusqu’ici mais je ne peux pas continuer à être en-dessous.\r\n\r\nPar exemple je vais animer un cursus en partenariat avec Microsoft et sur tous les stages je serai a 700 euros et\r\nMême si je pouvais demander plus je reste dans ma moyenne.','couleur','Stéphane',25,'','',700);
