INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (299,'5,  Chemin de la Pesotte      \n33138 Lanton\nTél. :\n06.61.81.05.94 \n\n\n',0,'IMC - VAELIA','','',NULL,'','arnaud@powershell-scripting.com',' PowerShell : 700€HT/jour sur IDF\nIl possède un support.','PETITJEAN','','','N\'est plus un INTERMEDIAIRE !\r\n\r\nReçu par LB le 15/10/09 pour parler de powershell. Pas de validation technique pure car il est auteur d’ouvrage et surtout certifié MVP (Most Valuable Professional) \npar Microsoft sur le sujet. Il peut utiliser son livre comme un support de cours (slides spécifiques). Il ne souhaite pas travailler plus de 10 jours par mois pour nous (à cause des A/R Paris-Bordeaux) et commençait à me demander combien de jours on pourrait lui garantir par mois. Il est déjà auto-entrepreneur et hésite (apparemment) à franchir le cap.\n\nAu CEA de Bordeaux il travaille sur beaucoup de sujet donc AD, 2003 Serveur, vmware et du coup me propose un cours powershell pour vmware qui devrait être intéressant en 2010 si la progression de vmware se confirme. Il manipule aussi des SAN donc je pense qu’il doit avoir un matériel énorme et une vrai expérience terrain.\n PowerShell : 700€HT/jour sur IDF\nIl possède un support.Concernant WSH, sauf cas particulier, je ne donne pas de formation. Etant donné mes disponibilités limitees, du fait que j\'exerce egalement une activite salariee, je prefere me concentrer sur PowerShell. \n\n\r\n\r\nLe 27/09 LB : Powershell\r\n700 avec support mais hors frais\r\n\r\nVmware :\r\n650 ( ?) avec support mais hors frais\r\n\r\nPour les frais il ne savait pas trop je lui ai proposé un forfait de 150 euros / jour ce qui ferait un prix de 800 pour sa prochaine intervention.\r\nMais il te fera une proposition\r\nIl  bosse pour 2 organismes de formation sur Bordeaux : Vaelia et IMC(boîte de Zavan)\r\n\r\nLe 25/02/11 \r\n\r\n-	tu ne souhaites plus te positionner sur le cours « VMware Administration avec PowerShell » \r\n-	si tu interviens pour PLB ton tarif sera de 700 euros hors frais (nous te remboursons sur la base des frais réels avec justificatifs)\r\n-	tu n’as pratiquement plus de dispo et il sera difficile de pouvoir travailler ensemble (congés sans solde de ton côté et ton employeur n’aime pas trop  …)\r\n-	nous pouvons solliciter Jacques Barathon directement (je ne sais pas où l’on en est des sessions inter 2011 mais je vais préciser à Karen de nous rapprocher de lui également pour ces sessions)','Contactés','Arnaud',50,'','',700);
