INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (566,'\n\n\n',0,'Pythagore','','',NULL,'','jacques@foucry.net','http://www.foucry.net/','Foucry','06 52 35 26 60','','Conseillé par Y. Cadin pour Unix avancé.\r\nMac OS X server - Unix\r\nPrise de contact by KD le 25/10\r\nRetour le jour même :\r\nJe suis l\'auteur de \"Mac OS X Server à votre service\" qui était jusqu\'à il y a peu le seul ouvrage sur le sujet\r\nJe ne dispose pas de support personnel hormis pour des formations Mac OS X.\r\nTout les environnements Unix (avec une prédilection pour Mac OS X et Linux, mais également Solaris), les logiciels OpenSource.\r\nLinux/Unix, logiciels OpenSource (Apache. MySQL).\r\nJe n\'ai pas de certifications, mais je vais prochainement commencer le processus de certification Apple.\r\n\r\nValidation du 24.11.10 by LB :\r\nGlobalement OK sur la technique même si parfois un peu rude dans les réponses quand il  ne les a pas en s’abritant derrière un argumentaire ressemblant à \r\n« les gens écrivent des choses qui ne servent à rien pour se démarquer des autres : qmail certain type de filesystem, … » \r\n\r\nEn régie chez Publicis Technology jusqu’au 22/12/2010 où il s’occupe de l’achat, installation et dépannage des MACS (volume ?)\r\n\r\nSon tiercé de compétences :\r\n1)	Admin UNIX (AIX mais moins HPUX), MAC et Linux (à valider pour Linux)\r\n2)	Opensource : Apache, LDAP, MySQL (un peu moins PHP mais pourrais animer un LAMP si pas trop de P)\r\n3)	Objective-C (a debrayé depuis 10 ans donc ne connaît pas les derniers SDK)\r\n\r\nBosse pour Pythagore donc il était salarié avant de tenter sa chance dans une startup (échec).\r\nIndépendant depuis mars 2009\r\n\r\nAuteur d’un ouvrage sur Mac OS X Server 10.5 qui a été pendant longtemps le seul en Français.\r\n\r\nDispose de support de cours sur Mac OS prise en main et Admin MAC OS niveau 1 (UMA1) même si Yannick Cadin à l’origine lui a fait parvenir le sien sur UMA1.\r\nLors de la validation UMA1 il n’avait amené que l’intro au MAC donc support UMA1 à pointer.\r\n\r\nATTENTION: EN MARS 2011 RECLAME UNE MAJORATION DE 11% SUR DES FACTURES NON REGLEES EN RETARD SELON LUI','','Jacques',50,'','',0);
