INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (588,'75 rue de lourmel\n\nPARIS 15\n',0,'Demos Next Formation Orsys','','',NULL,'','julien@sealskinproduction.fr','400€ HT/j Paris - 550 euros HT/j ProvinceSealskin Production','Duloutre','06 61 35 62 95','01 76 53 99 82','Tout ce qui est Adobe je vous place quelqu\'un rapidement.\r\nVidéo : je fais moi-même = mon domaine d\'expertise et ce pourquoi je suis certifié.\r\n\r\nVotre tarif d’intervention est de 400 euros HT / jour et de 450 euros HT / jour si vous mettez à disposition du matériel (caméra, micro, …).\r\nPour les déplacement en Province vous proposez un tarif de 550 euros HT / jour tout compris.\r\n\r\nJe suis donc Adobe Certified Instructor (ACI) sur PREMIERE PRO (Montage vidéo) et Encore (Authoring DVD). Certification en cours sur Final Cut Pro / DVD Studio Pro sur Mac.\r\n\r\nJe forme sur ces modules pour Demos, Mediabox, Regart.net, L’ecole Multimedia, Next Formation et Orsys.\r\n\r\nJe suis aussi l’auteur des formations DVD et VOD Premiere Element 8 et 9 (en cours) pour la société Elephorm.\r\n\r\nPremiere sur 3/5 jours\r\nEncore sur 3j\r\nFinal Cut 3/5 jours\r\nDVD Studio Pro 3J\r\n\r\nsuite RDV LB le 20.04.11 : Personnellement vos principales compétences concernent Final Cut, Première pro, et Authoring DVD mais vous pourriez facilement nous proposer d’autres intervenants selon les conditions financières précisées au dessus. Karen va vous transmettre une grille de compétences à compléter dans cet esprit.\r\n\r\n• 3D Studio Max Initiation   \r\n• 3D Studio Max Perfectionnement   \r\n• Indesign prise en main\r\n• Indesign  Pour les utilisateurs Xpress\r\n• Indesign Perfectionnement\r\n• Illustrator prise en main\r\n• Illustrator  perfectionnement\r\n• Photoshop - L\'essentiel (mise en œuvre rapide)\r\n• Photoshop - Une Introduction complète\r\n• Photoshop - Perfectionnement\r\n• Adobe Photoshop et la colorimétrie   \r\n• After effects - Niveau 1\r\n• After effects - Niveau 2\r\n• After effects - Motion graphic\r\n• Première Pro\r\n• Final Cut Pro Initiation   \r\n• Final Cut Pro Perfectionnement   \r\n• Motion - Niveau 1\r\n• Motion - Niveau 2','','Julien',50,'','',550);
INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (593,'\n\n\n',0,'','','',NULL,'','','','Avoustin','','','Trouvé sur le web by LB\r\nContact par viadeo en dec 2010 by KD :\r\nBonjour,\r\n\r\nMerci pour votre prise de contact.\r\nJe suis actuellement sur Paris, mais je viens de lancer un projet de déménagement sur Montpellier.\r\nCeci se fera dans le cadre d\'une activité différente, peut-être avec le statut d\'indépendant.\r\nDans le cadre de ma nouvelle activité, votre proposition peut m\'intéresser.\r\nMais comme vous l\'aurez compris, ce projet ne s\'est pas encore concrétisé.\r\nSi vous le souhaitez, nous pouvons garder contact.\r\nJe vous invite à me recontacter d\'ici 4 à 5 mois si d\'aventure je ne le faisais pas moi-même pour échanger à nouveau sur le sujet.\r\nJe serai alors plus à même d\'apporter des réponses concrètes à vos questions\r\n\r\nPROF Sharepoint','','Jerome',50,'','',0);
INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (594,'\n\n\n',0,'','','',NULL,'','','','Clauss','06 99 90 30 79','','Formateur Sketchup\r\nContact by KD le 6/12/2010 by Viadeo : \r\nMa formation initiale est celle d\'un architecte qui a pratiqué des logiciels de CAO-DAO tels Autocad, Archicad, Topcad , et multimédia tels Photoshop, Gimp et Paint.net. \r\n\r\nA présent, j\'interviens essentiellement sur le logiciel Google SketchUp version Pro ou gratuite. Suivant la demande, la formation peut également inclure:\r\n- maîtrise de LayOut et Style Builer, logiciels inclus dans la version Pro\r\n- présentation et apprentissage des principaux plugins permettant d\'optimiser SketchUp\r\n- présentation des moteurs de rendu gratuits tels Ketkythea et Pov-Ray\r\n- initiation au travail de post-processing à partir des fichiers graphiques de SketchUp vers des logiciels de traitement d\'images\r\n\r\nJe confectionne moi-même des supports de cours qui sont constitués par un mémento rappelant les principales fonctions et outils de SketchUp (environ 10 pages).\r\nJe vous précise que je suis libre les jeudis, les samedis et pendant les vacances scolaires de la zone B.','','Jean-Luc',50,'','',0);
