INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (610,'\n\n\n',0,'','','',NULL,'','hani.attalah@iviflo.com','','ATTALAH','06 14 37 94 03','','RTEE\r\nRTIP\r\nValidation RTEE 3.05.11 by LB : \r\n\r\nDonc comme vu ensemble impression réservée.\r\n\r\nJe détaille un peu pour moi\r\nManque de culture terrain, n’a jamais paramétré un serveur DHCP, ne connaît pas les informations spécifiques à la téléphonie que pourrait délivrer un serveur DHCP (@ du serveur SIP), pas clair sur le câblage d’une entreprise par rapport à son PABX (réutilisation des câbles RJ45),\r\nBref pas très fluide sur la partie qui fait finalement le cœur de ce cours (intelligence d’un téléphone, celle du PAXB s’arrête où ? liens avec les opérateurs, …)\r\n\r\nBref c’est le fait d’acheter un PABX pour PLB qui me fait poser des questions que je n’aurais pas poser avant mais c’est justement ce que l’on souhaitait dans ce cours …\r\n\r\nVoyons les évaluations d’IB pour trancher (il est intervenu à Brest et m’a dit que ça c’était bien passé  c’est pour ça que j’ai le doute …)\r\nTu essaies d’avoir rapidement les évaluations (semaine du 4 au 8 avril) sachant qu’il a animé le début et Luc la fin (ce qui confirme ses limites techniques)\r\nJe n’ai pas du voir passé cette planification car c’était pout IB. Tu glisseras un petit mot à Luc pour lui dire qu’il doit nous prévenir pour chaque baptême …\r\n\r\nJe vais quand même demander à Luc un backup (7 inscrits !) au cas où et lui faire part de mes réserves …\r\n(le 25/04/2012)\r\nEvaluations pas mauvaise mais fade : 3S / 3 , aucune remarque positive …\r\nPas de manipulation sur le PABX qui a du rester par terre alors qu’on lui avait descendu dans sa salle','','Hani',90,'','',0);
