INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (633,'1 Bis rue Feydeau\n\nBROU SUR CHANTEREINE\n',0,'','','',NULL,'','franck.yot@inovelia.com','PHIDELYS Consulting','YOT','06 32 18 57 60','01 60 20 89 84','Echange téléphonique entre LB et Franck Yot du 23/11/2011\r\n\r\nBonne impression, connaît bien la formation même si un peu trop rigoureux sur les pré-requis Control-M (2 ans d’exploitation minimum ! car ne connaissent pas assez l’admin, le réseau, et le shell)\r\nAssez bavard mais parle bien du produit Control-M (zoom technique OK, il fait de la prod depuis 25 ans)\r\nSon tarif serait de 850 euros quand on passera en direct (sinon voir le tarif proposé par BMC).\r\nIl m’invite à suivre les formations plutôt chez BMC pour ne pas avoir de souci de matériel/logiciel\r\n\r\nMe dit que les formations Control M sont l’exclusivité de fait de BMC Software car le logiciel coûte très cher (20 000 la licence de base) , mais on peut l’acheter et faire les formations.\r\nBMC propose des intervenants aux différentes sociétés ou centres de formations dont sa société à lui (INOVELIA).\r\nIls sont très pris et ne peuvent fournir que des demandes à l’avance.\r\nM’a donné les coordonnées de Mr Erwann Nedelec 01 57 00 61 00, erwann_nedelec@bnc.com habilité à me parler du produit et des modalités de collaboration.\r\nPeut proposer aussi des ressources Netbackup (sans support) ou TSM (il sous-traite)\r\n\r\n\r\nExpert Control-M \r\nExpert en Administration d\'Outils de \r\nProduction \r\nVirtualisation\r\n\r\nContacté by KD le 28.01.11\r\nDde sur Control M','contacté','Franck',50,'','',0);
