INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (635,'\n\n\n',0,'','','',NULL,'','roverselli@hotmail.com / gir@agilya.com','','ROVERSELLI','','','Gino Roverselli sur recommandation de Françon\r\nPremier contact le 7/2/2011 pour validation SQL Server 2008 de tournée court car … ne connaissait pas\r\nTravaille chez Agilya (boîte du frère de Françon sur Levallois) mais avec un rôle un peu flou car ne connaissait\r\npas du tout la filière oracle alors que se présente en responsable formation.\r\nCependant il a toutes les certifs Microsoft sur Windows 2008 server, et se trouve OK sur XP et 7\r\nNe connait pas la virutalisation et se met depuis peu à powershell.\r\nDevrait être OK sur les cours d’intro.\r\nFait aussi un peu de linux admin si pas trop poussé (certifié Suse)\r\nSe positionne complètement sur ghost, MSPC, et MSPS car vrai expérience terrain.\r\n\r\nValidation MAWS 22/02/11 - LB:\r\nValidé\r\n\r\nPas indépendant. Salarié de la boîte du frère de Françon \r\n\r\nOK sur les fondamentaux mais pas expert et plus léger dès qu’on sort un peu des standards.\r\nFerait un bon cours auprès d’un public normal et normal +.\r\n\r\nNe savait pas : si on pouvait auditer les actions d’un utilisateur, gestion des SAN, TP concret sur NAP (capable cependant de parler un peu d’IPsec, de mettre en place un VPN, …)\r\n\r\nBon relationnel doit être assez proche et à l’écoute des participants.\r\n\r\nDispose de toute l’armada de certifs Microsoft sur 2008.\r\nDemande lui d’ailleurs les liens …\r\n\r\nVa suivre une formation powershell prochainement.\r\n\r\nje n\'ai pas de date de libre avant me 11/07/11.','','Gino',50,'','',0);
