INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (655,'\n\n\n',0,'','','',NULL,'','samia.bouzefrane@cnam.fr','','Bouzefrane','','','Salariée de la CNAM\r\nrecommandée par François Horn\r\nPrise de contact tel par PB le 16/02/11\r\n\r\nEnvoi grille by KD le 17/02/11\r\n+dde de :  Votre programme de cours sur Java Card\r\n\r\nComme promis, voici ma proposition de cours Java Card.\r\nJe suppose que le groupe ne depassera pas une vingtaine de personnes (en supposant qu\'il y ait de la demande), car je dispose de 10 lecteurs de cartes SIM, de 15 lecteurs de Java Card. Par contre, les cartes j\'en ai assez.\r\nPour Java Card 3.0, c\'est un simulateur qu\'on utilise car il n\'existe pas encore de Java Card 3.0 sur le marche, c\'est encore au stade de prototype.\r\n\r\nJuste pour vous faire remarquer que je peux aussi dispenser une formation sur 2 jours avec une journee sur les cartes et une journee sur Java Card, incluant a chaque fois une partie cours et une partie TP.\r\n\r\nLes cours que je peux assurer sont ceux que je dispense au CNAM et dans les ecoles d\'ingenieur a savoir :\r\n\r\n- La programmation systeme sous Unix/Linux inspiree d\'un livre que j\'ai edite en 2003 chez Dunod (http://www.dunod.com/auteur/samia-bouzefrane)\r\n\r\n- Les systemes temps reel avec une programmation en Java temps reel\r\n\r\n- Les cartes a puce et la programmation Java Card\r\n\r\nTarif : 900€ mais en salaire net, soit 1.552\r\nConcernant mon statut, j\'ai fait ma declaration de consultant independant, j\'attends que l\'URSSAF m\'envoie ce qu\'il faut.','','Samia',50,'','',800);
