INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (772,'\n\n\n',0,'','','',NULL,'','gilles.blanc@linacs-consulting.com','800http://gillesblanc.com','Blanc','06 70 33 00 03','','N\'est plus chez LINAGORA\r\nGestion de projet embarqué Linux embarqué\r\nPython avancé\r\nPython embarqué et industriel\r\n\r\necherchesClaudia\r\nLinux embarqué\r\nLinux driver\r\n\r\nN\'est plus chez Linagora depuis 2012 - AD :\r\n\r\nBonjour M. Blanc,\r\n\r\nMerci pour ces informations, je vais prendre contact avec M. Foubet.\r\n\r\nEn ce qui vous concerne, en attendant la stabilisation de la situation avec Linagora, est-il envisageable (sur une semaine de congés par exemple) de vous proposer d\'animer des sessions pour initier notre collaboration de manière concrète?\r\n\r\nSinon, je vous laisse revenir vers nous lorsque nous pouvons démarrer.\r\n\r\nVous mes flattez, décidément. J\'adorerais, mais je suis confronté à deux problèmes :\r\n* mes clauses de non-concurrence et d\'exclusivité (j\'ai une dérogation pour les cours en école d\'ingénieur... le samedi -- d\'où le fait que j\'ai arrêté d\'en donner)\r\n* mes jours de congés sont peu nombreux (pour tout dire, il en\r\nmanque...) et quasiment tous imposés, impossible pour moi de prendre plus d\'un jour de RTT tous les deux mois en dehors des périodes de décembre et de juillet...\r\nMerci, je penserai à vous si ma situation s\'amène à changer vers plus de liberté, pour ne pas dire d\'indépendance…\r\n\r\n> Avez-vous désormais un statut indépendant.\r\n\r\nPas encore tout à fait : ma société est en formation, je suis en attente administrative qui peut encore durer 1 mois à 1 mois et demi.\r\nPour le moment, j\'ai deux (voire trois, il faut que je vérifie) solutions pour intervenir :\r\n_ passer en sous-traitance auprès d\'une société amie qui s\'est proposée de facturer à ma place, et avec laquelle je m\'arrange pour rétro-facturer dès que j\'aurai monté ma structure ; _ me salarier en tant que formateur occasionnel (je pense que vous devez pratiquer ?) ; _ voire créer rapidement une structure AE/EIRL (mais je dois d\'abord en parler aux ASSEDIC, pour être certain que ça ne me fera pas sauter des aides futures).\r\n\r\nSinon, tout devrait être normalisé d\'ici fin novembre (sous réserve que mon immatriculation ne prenne pas trop de temps). En attendant, nous pouvons dans tous les cas planifier, nous rencontrer, etc.\r\n\r\n\r\n> Avez-vous commencé à animer des formations en votre nom?\r\n\r\nPas encore avec ma propre société, de fait. Mais je propose dores et déjà des interventions possibles sur :\r\n_ Linux embarqué ;\r\n_ C/système (programmation système) ;\r\n_ Python.','web','Gilles',50,'','',800);
