INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (862,'\n\n\n',0,'Pythagore Atos Bull','','',NULL,'','alex@euronode.com','PAR PYTHAGORE IMPERATIF','Guy','05 62 47 15 53','','Unix\r\nTrès bien noté\r\nTrouvé by LB sur le web : \"Réseau privé virtuel authentification sécurisée\"\r\n\r\nLa sécurité du système d\'exploitation GNU/Linux et des réseaux informatiques font parti de mes spécialités.\r\n\r\nMon domaine de compétences sont les suivants :\r\n- Systèmes d\'exploitation : GNU/Linux (de débutant à expert, distributions Redhat/Centos et Debian/Ubuntu), Solaris, AIX et HPUX (administration courante);\r\n- Réseaux informatiques : couches 1 à 7, théorie et pratique (je suis ingénieur télécoms & réseaux)\r\n- Sécurité système et réseau\r\n- Langages : ASM, C, Perl, Python, Shell ...\r\n- Serveur d\'application Zope / Plone\r\n- Bases de données : expert Mysql, admin courante Postgresql\r\n- expert Annuaires LDAP\r\n- administration avancée de tous les services réseaux courants : Apache, Postfix, Bind, DHCP, SSH, etc.\r\n- expert Supervision Nagios et métrologie Cacti; déploiement Centreon, clusters Nagios, développement de plugins, SNMP, etc.\r\n\r\n600 € HT / jour, paiement à 60 jours, formations de 3 jours minimum.\r\n\r\nJe dispose d\'un support de cours personnel sur l\'administration système et réseau GNU/Linux.\r\n\r\nPour les autres formations, le support de cours est fourni par le centre de formation, il s\'agit :\r\n- soit d\'un livre (souvent les supports ENI)\r\n- soit d\'un support de cours sur mesure conçu par le centre de formation (sujets spécifiques et poussés, comme l\'administration avancée Unix, la sécurité Linux et réseau ...)\r\n\r\nVoter cœur de compétences se situe dans le domaine de l’open source.\r\nPlus particulièrement autour de la supervision (Nagios, Cacti, Centreon), la sécurité réseau, TCP/IP,  l’administration Linux (tous niveaux) ou PostgreSQL (niveau 1).\r\nD’autres domaines comme le Wifi et la sécurité ou encore Python vous intéressent également.\r\n\r\nDepuis plus récemment vous vous intéressez aux systèmes UNIX et vous aimeriez développer cette compétence.\r\n\r\nSollicité pour SF2I Noumea via Pythagore août Nagios\r\n\r\nJ\'ai appelé Helmer et j\'ai bien fait.\r\n\r\nElle n\'appréciait pas trop qu\'on passe en direct avec Guy même si elle comprenait qu\'on l\'ait trouvé sur le WEB sans fouiner dans les évales.\r\nIl fait partie des murs chez elle.\r\nPour des profs plus occasionnels ça ne la dérange pas.\r\n\r\nElle me rappelait l\'épisode Dutreix où on lui avait \"imposé\" de passer par nous.\r\n\r\n\r\nEn plus à 710 euros / jour en plus ce n\'est pas gênant de passer par elle et on garde nos bons rapports.\r\n\r\nqu’il ne fait pas. »tuning MySQL » mais demande à Helmer si Mr Guy peut faire un cours MySQL admin avancée (car du coup j’ai le doute pour celui là aussi)','Vu à la TV','Alexandre',30,'','',0);
