INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (1026,'\n\n\n',0,'','','',NULL,'','jp@vincent.net','','Vincent','','','Rencontré par LB fin septembre 2011 pour discuter collaboration.\r\nMalgré la co-écriture d’un bouquin sur HTML 5 ne connaît pas les canevas, pas d’expérience en formation mais bon potentiel.\r\nLB lui propose de venir assister à une première journée …\r\nSollicité pour écrire un programme sur les perfs des sites WEB\r\n\r\n\r\ngestion d\'équipe technique, projets Web techniquement avancés, performances Web, AJAX, HTML5, PHP5, Javascript avancé, Orateur, veille technologique\r\n\r\nExpert applications Web : HTML5, PHP5, javascript\r\n\r\nPrise de contact by KD le 14.09.11\r\nAnimation HTML 5\r\n+ une proposition de transfert le 23.09.11\r\n\r\nRetour :\r\n\r\noncernant mes références :\r\n- j\'ai déjà une grosse base structurée et écrite sur HTML5 car j\'ai récemment sorti un livre sur HTML5 : http://www.amazon.fr/HTML5-page-web-%C3%A0-lapplication/dp/2100565281/ref=sr_1_1?ie=UTF8&qid=1313658566&sr=8-1\r\n- je n\'ai jamais donné de formation, cependant je me suis déjà essayé plusieurs fois à l\'exercice de la conférence notamment sur HTML5 : http://braincracking.org/2011/03/01/html5-maintenant-chapitrage-de-la-conference/\r\n\r\nConcernant la demande ponctuelle sur Canvas, je ne vais pas pouvoir aborder le sujet immédiatement, car il faut que je l\'explore plus en profondeur avant de me sentir suffisamment à l\'aise pour dispenser une formation\r\n\r\nJe suis actuellement en train de chercher à changer de parcours professionnel, j\'étais donc justement en train d\'étudier la possibilité de faire de la formation. J\'ai fixé mon tarif / jour à 600€ et lorsque je me lancerais je serais probablement en indépendant.\r\n\r\nJe peux intervenir sur\r\n- HTML5 en général \r\n- le développement d\'applications riches en JavaScript\r\n- les performances Web qui est une spécialité montante regroupant des techniques pour accélérer l\'affichage des pages\r\n\r\n\r\nJ\'aimerais savoir si de votre côté vous avez déjà des demandes fortes pour remplir l\'atelier HTML5, et si oui sous quel délai vous souhaitez le voir mis en oeuvre. Je suis actuellement en déplacement à l\'étranger, aussi si mon parcours vous intéresse nous pourrions nous rencontrer dès la semaine prochaine ?','Contacté','Jean-Pierre',50,'','',800);
