INSERT INTO `Intervenant` (`id`,`adresse`,`anglais`,`centres`,`certifications`,`conditionGenerale`,`dateMisAJour`,`delegation`,`email`,`infoTarif`,`nom`,`numPortable`,`numTel`,`observations`,`origine`,`prenom`,`rang`,`statut`,`statutAutre`,`tarif`) VALUES (1257,'\n\n\n',0,'','','',NULL,'','','','Andraws','','','VALIDATION LB :  ! NON !\r\n\r\nUn rendez-vous de validation oublié pourtant confirmé il a quelques jours  je t’avoue que ça n’était pas fait pour me rassurer sur un plan purement organisationnel.\r\n\r\nSur le plan technique je n’ai pas été vraiment rassuré non plus.\r\nJe recherchais un Administrateur SQL Server et son profil est plus généraliste et développeur.\r\nIl revenait souvent sur l’importance de la conception et ça donnait une impression de (bon) prof d’école plus que de personne ayant un vécu en exploitation.\r\nIl ne connaissait pas le RAID 1 lorsqu’il me parlait de miroir pour SQL Server, il confondait l’authentification mixte de SQL Server avec des problèmes de login liés au firewall (port 1433 fermé par défaut) et il n’était pas vraiment capable de me commenter des options  de la commande backup qui sont pourtant celles générées par défaut lorsque l’on fait une sauvegarde avec l’interface graphique (DBA Management Studio).\r\n\r\nBref je préfère ne pas le valider, en tout cas pas sur de l’administration pure.','','',99,'','',0);
